mapper.select:
select

from camera_info


		name = #{name,jdbcType=VARCHAR}


		and serial = #{serial,jdbcType=VARCHAR}


		and device_id = #{deviceId,jdbcType=VARCHAR}


		and mac_addr = #{macAddr,jdbcType=VARCHAR}


		and pub_net_addr = #{pubNetAddr,jdbcType=VARCHAR}


		and lan_net_addr = #{lanNetAddr,jdbcType=VARCHAR}


		and type = #{type,jdbcType=VARCHAR}


		and location_addr = #{locationAddr,jdbcType=VARCHAR}


		and status = #{status,jdbcType=BIGINT}


		and desc = #{desc,jdbcType=VARCHAR}


		and creater = #{creater,jdbcType=VARCHAR}


		and modifier = #{modifier,jdbcType=VARCHAR}


		and modify_time = #{modifyTime,jdbcType=TIMESTAMP}


		and create_time = #{createTime,jdbcType=TIMESTAMP}
